import React, { useState, useEffect, useRef, useCallback } from 'react';
import * as htmlToImage from 'html-to-image';
import './PrintLayout.css';

// Î∞∞Ïó¥ÏùÑ ÎûúÎç§ÌïòÍ≤å ÏÑûÎäî Ìó¨Ìçº Ìï®Ïàò
const shuffleArray = (array) => {
  if (!array) return [];
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
  }
  return newArray;
};

// Î∂ÑÎã® Ïù∏Îç±Ïä§ Í≥ÑÏÇ∞ Ìï®Ïàò
const getSectionIndex = (colIndex, aisles) => {
  let section = 0;
  const sortedAisles = [...aisles].sort((a, b) => a - b);
  for (const aisleIndex of sortedAisles) {
    if (colIndex > aisleIndex) {
      section++;
    }
  }
  return section;
};

const PrintLayout = ({ settings, seatData, onBack }) => {
  const [isShuffling, setIsShuffling] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [countdown, setCountdown] = useState(10);
  const [currentGrid, setCurrentGrid] = useState(null);
  const [displayType, setDisplayType] = useState('nameAndId'); // 'nameAndId', 'nameOnly', 'idOnly'
  const [isFlipped, setIsFlipped] = useState(false);

  const printAreaRef = useRef(null);
  const animationFrameRef = useRef(null);
  const countdownIntervalRef = useRef(null);

  useEffect(() => {
    if (!seatData?.grid) {
        setCurrentGrid([]);
        setIsShuffling(false);
        return;
    }

    if (!seatData.students || seatData.students.length === 0) {
      setIsShuffling(false);
      setCurrentGrid(seatData.grid);
      return;
    }

    // skipAnimationÏù¥ trueÎ©¥ Ïï†ÎãàÎ©îÏù¥ÏÖò ÏóÜÏù¥ Ï¶âÏãú ÌëúÏãú
    if (seatData.skipAnimation) {
      setIsShuffling(false);
      setCurrentGrid(seatData.grid);
      return;
    }

    const studentList = seatData.students;
    const duration = 10000;
    const startTime = Date.now();

    const animate = () => {
      const shuffledStudents = shuffleArray(studentList);
      let studentIndex = 0;
      const newGrid = seatData.grid.map(row =>
        row.map(seat => {
          if (seat.occupied) {
            const randomStudent = shuffledStudents[studentIndex % shuffledStudents.length];
            studentIndex++;
            return { ...seat, studentName: randomStudent.name, studentId: randomStudent.number };
          }
          return seat;
        })
      );
      setCurrentGrid(newGrid);
      animationFrameRef.current = setTimeout(animate, 150);
    };

    setCurrentGrid(seatData.grid);
    animationFrameRef.current = setTimeout(animate, 150);

    countdownIntervalRef.current = setInterval(() => {
      const elapsedTime = Date.now() - startTime;
      const remainingTime = Math.max(0, duration - elapsedTime);
      setCountdown(Math.ceil(remainingTime / 1000));
      if (remainingTime <= 0) {
        if (animationFrameRef.current) clearTimeout(animationFrameRef.current);
        clearInterval(countdownIntervalRef.current);
        setIsShuffling(false);
        setCurrentGrid(seatData.grid);
      }
    }, 1000);

    return () => {
      if (animationFrameRef.current) clearTimeout(animationFrameRef.current);
      if (countdownIntervalRef.current) clearInterval(countdownIntervalRef.current);
    };
  }, [seatData]);

  const handlePrint = () => { window.print(); };

  const handleSaveAsImage = useCallback(() => {
    const container = printAreaRef.current;
    if (container === null) return;

    // Ïã§Ï†ú ÏΩòÌÖêÏ∏† ÏòÅÏó≠Îßå Ï∫°Ï≤òÌïòÍ∏∞ ÏúÑÌï¥ page-content-wrapperÎ•º ÌÉÄÍ≤üÏúºÎ°ú ÏÑ§Ï†ï
    const contentWrapper = container.querySelector('.page-content-wrapper');
    if (!contentWrapper) return;

    setIsSaving(true);

    const date = new Date();
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const dateString = `${year}${month}${day}`;
    const filename = `${settings.grade}ÌïôÎÖÑ ${settings.classNumber}Î∞ò ÏûêÎ¶¨Î∞∞ÏπòÌëú(${dateString}).png`;

    // A4 ÎπÑÏú®Ïóê ÎßûÍ≤å Ïó¨Î∞±ÏùÑ Ï∂îÍ∞ÄÌïú ÌÅ¨Í∏∞ Í≥ÑÏÇ∞
    const mmToPx = (mm) => (mm / 25.4) * 96; // 96 DPI Í∏∞Ï§Ä
    const A4_WIDTH_PX = Math.round(mmToPx(210)); // 794px
    const A4_HEIGHT_PX = Math.round(mmToPx(297)); // 1123px
    const MARGIN_PX = Math.round(mmToPx(20)); // 20mm Ïó¨Î∞±

    // page-content-wrapperÎ•º A4 ÌÅ¨Í∏∞Ïóê ÎßûÍ≤å Ï∫°Ï≤ò
    htmlToImage.toPng(contentWrapper, { 
      cacheBust: true, 
      backgroundColor: '#ffffff', 
      pixelRatio: 2,
      // A4 ÌÅ¨Í∏∞Ïóê Ïó¨Î∞±ÏùÑ Í≥†Î†§Ìïú ÌÅ¨Í∏∞Î°ú Ï∫°Ï≤ò
      width: A4_WIDTH_PX,
      height: A4_HEIGHT_PX,
      style: {
        padding: `${MARGIN_PX}px`,
        boxSizing: 'border-box',
        width: A4_WIDTH_PX + 'px',
        height: A4_HEIGHT_PX + 'px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'center'
      }
    })
      .then((dataUrl) => {
        const link = document.createElement('a');
        link.download = filename;
        link.href = dataUrl;
        link.click();
      })
      .catch((err) => { console.error('Ïù¥ÎØ∏ÏßÄ Î≥ÄÌôòÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.', err); })
      .finally(() => {
        setIsSaving(false);
      });
  }, [printAreaRef, settings.grade, settings.classNumber]);

  if (!currentGrid) {
    return (
      <div className="print-layout-final">
        <div className="error-message"><h2>ÏûêÎ¶¨ Î∞∞Ïπò Îç∞Ïù¥ÌÑ∞Î•º Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§...</h2></div>
      </div>
    );
  }

  const displayGrid = isFlipped && currentGrid ? [...currentGrid].reverse().map(row => [...row].reverse()) : currentGrid;

  const seatingGrid = displayGrid ? (
    <div className="print-seating-area-final">
      <div className="print-seat-grid-final">
        {displayGrid.map((row, rowIndex) => (
          <div key={rowIndex} className="print-seat-row-final">
            {row.map((seat, colIndex) => {
              const sectionIndex = getSectionIndex(colIndex, seatData.verticalAisles);
              
              const nameStyle = {};
              let displayName = seat.studentName;
              const nameClasses = ['student-name-final'];

              if (displayType === 'nameOnly' && seat.studentName) {
                if (seat.studentName.length >= 4) {
                  nameStyle.fontSize = '0.8em';
                } else if (seat.studentName.length === 2) {
                  displayName = seat.studentName.split('').join('  ');
                  nameClasses.push('short-name-2');
                }
              }

              const infoStyle = {};
              if (displayType === 'nameOnly' && seat.studentName && seat.studentName.length >= 4) {
                  const horizontalPadding = Math.max(0, 5 - (seat.studentName.length - 3) * 1);
                  infoStyle['--name-padding-x'] = `${horizontalPadding}px`;
              }

              return (
                <React.Fragment key={colIndex}>
                  {colIndex > 0 && seatData.verticalAisles.includes(colIndex - 1) && (
                    <div className="print-vertical-aisle-final"></div>
                  )}
                  <div 
                    className={`print-seat-final ${seat.occupied ? 'occupied' : 'hidden'}`}
                    data-section-index={sectionIndex}
                  >
                    {seat.occupied && (
                      <div className={`print-student-info-final display-${displayType}`}
                           style={infoStyle}
                      >
                        {displayType !== 'idOnly' && <div className={nameClasses.join(' ')} style={nameStyle}>{displayName}</div>}
                        {displayType !== 'nameOnly' && <div className="student-id-final">{seat.studentId}</div>}
                      </div>
                    )}
                  </div>
                </React.Fragment>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  ) : null;

  return (
    <div className={`print-layout-final ${isShuffling ? 'shuffling' : ''}`}>
      {isSaving && (
        <div className="saving-overlay">
          <div className="saving-message">Ï°∞Í∏àÎßå Í∏∞Îã§Î¶¨ÏãúÎ©¥ Îã§Ïö¥Î°úÎìúÍ∞Ä ÏãúÏûëÎê©ÎãàÎã§.</div>
        </div>
      )}
      <div className={`countdown-overlay ${isShuffling ? 'show' : ''}`}>
        <div className="countdown-number">{countdown}</div>
      </div>

      <div className="print-controls-wrapper no-print">
        <div className="print-controls">
            <button onClick={onBack} className="back-btn">‚Üê Î©îÏù∏ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
            <button onClick={handleSaveAsImage} className="save-btn" disabled={isSaving}>üñºÔ∏è Ïù¥ÎØ∏ÏßÄÎ°ú Ï†ÄÏû•</button>
            <button onClick={handlePrint} className="print-btn">üñ®Ô∏è Ï∂úÎ†•ÌïòÍ∏∞</button>
        </div>
        <div className="display-options">
            <button className={`option-btn name-id-option ${displayType === 'nameAndId' ? 'active' : ''}`} onClick={() => setDisplayType('nameAndId')}>ÌïôÎ≤à+Ïù¥Î¶Ñ</button>
            <button className={`option-btn name-only-option ${displayType === 'nameOnly' ? 'active' : ''}`} onClick={() => setDisplayType('nameOnly')}>Ïù¥Î¶ÑÎßå</button>
            <button className={`option-btn id-only-option ${displayType === 'idOnly' ? 'active' : ''}`} onClick={() => setDisplayType('idOnly')}>ÌïôÎ≤àÎßå</button>
            <button className="option-btn flip-btn" onClick={() => setIsFlipped(!isFlipped)}>üîÑ ÏÉÅÌïò Î≥ÄÍ≤Ω</button>
        </div>
      </div>

      <div className="a4-container-final" ref={printAreaRef}>
        <div className="page-content-wrapper">
          <header className="print-header-final">
            <h1>{settings.grade}ÌïôÎÖÑ {settings.classNumber}Î∞ò ÏûêÎ¶¨Î∞∞ÏπòÌëú</h1>
            <p className="print-date-final">{new Date().toLocaleDateString('ko-KR', { year: 'numeric', month: 'long', day: 'numeric' })}</p>
          </header>

          <main className="print-classroom-final">
            {isFlipped ? (
              <>
                <div className="classroom-label-bottom">ÍµêÌÉÅ</div>
                {seatingGrid}
                <div className="classroom-label-top">ÍµêÏã§ Îí§</div>
              </>
            ) : (
              <>
                <div className="classroom-label-top">ÍµêÏã§ Îí§</div>
                {seatingGrid}
                <div className="classroom-label-bottom">ÍµêÌÉÅ</div>
              </>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default PrintLayout;
