import React, { useState } from 'react';
import './App.css';
import ControlPanel from './components/ControlPanel';
import ClassroomLayout from './components/ClassroomLayout';
import PrintLayout from './components/PrintLayout';

function App() {
  const [classSettings, setClassSettings] = useState({
    grade: 1,
    classNumber: 1,
    studentCount: 24,
    maleCount: 12,
    femaleCount: 12,
    pairingType: 'mixed', // 'mixed', 'male', 'female', 'single'
    seatArrangement: 'pair' // 'pair', 'single'
  });
  const [students, setStudents] = useState([]); // ÌïôÏÉù Î™©Î°ù ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [currentPage, setCurrentPage] = useState('main'); // 'main', 'print'
  const [seatData, setSeatData] = useState(null); // ÏûêÎ¶¨ Î∞∞Ïπò Îç∞Ïù¥ÌÑ∞
  const [teacherNotes, setTeacherNotes] = useState({
    togetherStudents: [],
    separateStudents: [],
    considerationStudents: [],
  }); // ÍµêÏÇ¨ Ï∞∏Í≥†ÏÇ¨Ìï≠ ÏÉÅÌÉú Ï∂îÍ∞Ä

  const handleSettingsChange = (newSettings) => {
    if (newSettings.students) {
      setStudents(newSettings.students);
      // ÌïôÏÉù Íµ¨ÏÑ±Ïù¥ Î∞îÎÄåÎ©¥ Í∏∞Ï°¥ Î∞∞Ïπò Îç∞Ïù¥ÌÑ∞ÏôÄ ÍµêÏÇ¨ Ï∞∏Í≥†ÏÇ¨Ìï≠ Ï¥àÍ∏∞Ìôî
      setSeatData(null);
      setTeacherNotes({
        togetherStudents: [],
        separateStudents: [],
        considerationStudents: [],
      });
      // studentsÎäî classSettingsÏôÄ Î≥ÑÎèÑÎ°ú Í¥ÄÎ¶¨
      const { students, ...restSettings } = newSettings;
      setClassSettings(prev => ({ ...prev, ...restSettings }));
    } else {
      setClassSettings(prev => ({ ...prev, ...newSettings }));
      // ÌïôÍ∏â ÏÑ§Ï†ïÏù¥ Î∞îÎÄåÏñ¥ÎèÑ Í∏∞Ï°¥ Î∞∞Ïπò Îç∞Ïù¥ÌÑ∞ Ï¥àÍ∏∞Ìôî
      setSeatData(null);
    }
  };

  const handleRandomArrangement = (generatedSeatData) => {
    setSeatData(generatedSeatData);
    setCurrentPage('print');
  };

  const handleBackToMain = () => {
    setCurrentPage('main');
  };

  const handleViewResults = (isViewOnly = false) => {
    if (seatData) {
      if (isViewOnly) {
        // Î∞∞Ïπò Îã§ÏãúÎ≥¥Í∏∞: Ï¶âÏãú Í≤∞Í≥º ÌëúÏãú (Ïï†ÎãàÎ©îÏù¥ÏÖò ÏóÜÏùå)
        setSeatData({ ...seatData, skipAnimation: true });
        setCurrentPage('print');
      } else {
        // Ïû¨Î∞∞ÏπòÌïòÍ∏∞: Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞ Ï¥àÍ∏∞Ìôî ÌõÑ ÏÉàÎ°ú Î∞∞Ïπò ÏöîÏ≤≠
        setSeatData(null); // Í∏∞Ï°¥ Î∞∞Ïπò Îç∞Ïù¥ÌÑ∞ Ï¥àÍ∏∞Ìôî
        // ClassroomLayoutÏóêÏÑú ÏÉàÎ°úÏö¥ Î∞∞Ïπò ÏÉùÏÑ±ÏùÑ Ìä∏Î¶¨Í±∞ÌïòÍ∏∞ ÏúÑÌï¥ ÌéòÏù¥ÏßÄÎ•º Ïú†ÏßÄ
      }
    }
  };

  const handleResetArrangement = () => {
    setSeatData(null);
  };

  if (currentPage === 'print') {
    return (
      <PrintLayout 
        settings={classSettings} 
        seatData={seatData}
        onBack={handleBackToMain}
        students={students}
      />
    );
  }

  return (
    <div className="App">
      <header className="app-header">
        <h1>üè´ ÌïôÍ∏â ÏûêÎ¶¨Î∞∞Ïπò ÎèÑÏö∞ÎØ∏</h1>
        <p className="copyright">‚ìí HooniKim All rights reserved</p>
      </header>

      <main className="app-main">
        <ControlPanel settings={classSettings} onSettingsChange={handleSettingsChange} students={students} />
        <ClassroomLayout 
          settings={classSettings} 
          students={students}
          onRandomArrangement={handleRandomArrangement}
          seatData={seatData}
          onViewResults={handleViewResults}
          onResetArrangement={handleResetArrangement}
          teacherNotes={teacherNotes}
          onTeacherNotesChange={setTeacherNotes}
        />
      </main>
    </div>
  );
}

export default App;
